import requestsBASE_URL = 'https://pokeapi.co/api/v2/pokemon'def fetch_data(name=None):    if name is None:        name = input("Bitte geben Sie den Namen des Pokemons ein: ")    response = requests.get(f'{BASE_URL}/{name}')        if response.status_code == 200:        data = response.json()        return data    else:        print(f"Es hat einen Fehler beim Abrufen der Daten f√ºr {name} gegeben. Vielleicht hast du dich vertippt?")        return Nonedef get_all_pokemon():    url = "https://pokeapi.co/api/v2/pokemon?limit=100"    response = requests.get(url)    if response.status_code == 200:        return response.json()['results']    else:        return None